import http.client
import json
import requests

url = "https://api.ebay.com/buy/browse/v1/item_summary/search?q=Nike%20SB%20MF%20DOOM&limit=10"

payload = {}
headers = {
  'Authorization': 'Bearer v^1.1#i^1#r^0#p^1#I^3#f^0#t^'
}

response = requests.request("GET", url, headers=headers, data=payload)

print(response.json())

